import React, { useState, useEffect, Component } from "react";
import {
	StyleSheet,
	View,
	Text,
	Image,
	TouchableOpacity,
	Platform,
	PermissionsAndroid,
} from "react-native";
import MapView, {
	Marker,
	AnimatedRegion,
	Polyline,
	PROVIDER_GOOGLE,
} from "react-native-maps";
import * as Location from "expo-location";
import haversine from "haversine";
import Ionicons from "@expo/vector-icons/Ionicons";
import { MaterialIcons } from "@expo/vector-icons";
import { colors, icons } from "../constants";

const styles = StyleSheet.create({
	container: {
		...StyleSheet.absoluteFillObject,
		justifyContent: "flex-end",
		alignItems: "center",
	},
	map: {
		...StyleSheet.absoluteFillObject,
	},
	bubble: {
		flex: 1,
		backgroundColor: "rgba(255,255,255,0.7)",
		paddingHorizontal: 18,
		paddingVertical: 12,
		borderRadius: 20,
	},
	latlng: {
		width: 200,
		alignItems: "stretch",
	},
	button: {
		width: 80,
		paddingHorizontal: 12,
		alignItems: "center",
		marginHorizontal: 10,
	},
	buttonContainer: {
		flexDirection: "row",
		marginVertical: 20,
		backgroundColor: "transparent",
		// backgroundColor: "red",
	},
});

const LATITUDE_DELTA = 0.009;
const LONGITUDE_DELTA = 0.009;
const LATITUDE = 37.78825;
const LONGITUDE = -122.4324;

// Set minimum distance to update data
const MIN_DISTANCE = 0.002; // (km)

function MyMapScreen(props) {
	const [errorMsg, setErrorMsg] = useState(null);

	const [followingButton, setFollwingButton] = useState({
		following: true,
		name: "gps-fixed",
	});

	//
	const [prevCoordinate, setPrevCroodinate] = useState({});
	const [routeCoordinates, setRouteCoordinates] = useState([]);
	const [distanceTraveled, setDistanceTraveled] = useState(0);
	const [coordinate, setCoordinate] = useState(
		new AnimatedRegion({
			latitude: LATITUDE,
			longitude: LONGITUDE,
			latitudeDelta: 0,
			longitudeDelta: 0,
		})
	);

	// Set coordinates of the region
	const getMapRegion = () => ({
		latitude: position.coords.latitude,
		longitude: position.coords.longitude,
		latitudeDelta: position.coords.latitudeDelta,
		longitudeDelta: position.coords.longitudeDelta,
	});

	// Calculate distance between previous and current position
	const calcDistance = (newCoords) => {
		return haversine(prevCoordinate, newCoords) || 0; // unit: km
	};

	// Request permission right after starting the app
	useEffect(() => {
		// After 1s, recall inner function
		let posotionInterval = setInterval(async () => {
			let { status } = await Location.requestForegroundPermissionsAsync();
			if (status !== "granted") {
				setErrorMsg("Permission to access location was denied");
				return;
			}
			let position = await Location.getCurrentPositionAsync({});
			console.log("===================== start ========================");

			// console.log(position);
			const { latitude, longitude } = position.coords;
			const newCoords = { latitude, longitude };

			// Calculate distance between new and old coordinates - unit: km
			let distanceCalculate = calcDistance(newCoords);
			console.log(distanceCalculate);
			if (distanceCalculate === 0 || distanceCalculate >= MIN_DISTANCE) {
				// After calculating distance, current coordinate has no value, it's
				// set all values
				setRouteCoordinates(routeCoordinates.concat([newCoords]));
				setPrevCroodinate(newCoords);
				setDistanceTraveled(distanceTraveled + distanceCalculate);
				setCoordinate(new AnimatedRegion(position.coords));
			}
		}, 1000);
		return () => clearInterval(posotionInterval);
	}, [routeCoordinates, distanceTraveled, prevCoordinate, coordinate]); // ;

	return (
		<View style={styles.container}>
			<MapView
				style={styles.map}
				showsUserLocation={true}
				followsUserLocation={followingButton.following}
				loadingEnabled={true}
				// region={getMapRegion()}
			>
				<View
					style={{
						marginTop: 40,
						flexDirection: "row",
						alignItems: "center",
						justifyContent: "flex-end",
						backgroundColor: "green",
					}}
				>
					<TouchableOpacity
						onPress={() => {
							if (followingButton.following === true) {
								setFollwingButton({
									following: false,
									name: "gps-not-fixed",
								});
							} else {
								setFollwingButton({
									following: true,
									name: "gps-fixed",
								});
							}
						}}
						style={{
							width: 50,
							height: 50,
							// backgroundColor: "transparent",
							backgroundColor: "red",
							alignItems: "center",
							justifyContent: "center",
						}}
					>
						<MaterialIcons
							name={followingButton.name}
							size={30}
							color={colors.locate}
						/>
					</TouchableOpacity>
				</View>

				<Polyline
					coordinates={routeCoordinates}
					strokeWidth={5}
					strokeColor={"blue"}
				/>
				<Marker.Animated coordinate={coordinate} />
			</MapView>
			<View style={styles.buttonContainer}>
				<TouchableOpacity style={[styles.bubble, styles.button]}>
					<Text style={{ fontSize: 24 }}>
						{parseFloat(distanceTraveled).toFixed(2)} km
					</Text>
				</TouchableOpacity>
			</View>
		</View>
	);
}

export default MyMapScreen;
