import React, { useState, useEffect, useRef } from "react";
import { View, Text, TouchableOpacity } from "react-native";

function UseHooks() {
	const [orderCount, setCount] = useState({ count: 0 });

	const [currentSecond, setCurrentSecond] = useState(null);
	const [distanceTimed, setDistanceTimed] = useState(0);
	const [routerTimes, setRouterTimes] = useState([]);
	const [prevSecond, setPrevSecond] = useState(null);

	const fetchData = async () => {
		let present = Date.now() / 1000;
		if (present - currentSecond >= 1) {
			setPrevSecond(currentSecond);
			setRouterTimes(routerTimes.concat[currentSecond]);
			setCurrentSecond(present);
		}
	};

	useEffect(() => {
		if (calculateTimed() >= 1.0 || distanceTimed === 0) {
			setPrevSecond(currentSecond);
			setRouterTimes(routerTimes.concat[currentSecond]);
			setCurrentSecond(Date.now() / 1000);
		}

		console.log(currentSecond);
	});

	return (
		<View
			style={{
				justifyContent: "center",
				alignItems: "center",
				flex: 1,
				backgroundColor: "purple",
			}}
		>
			<Text style={{ fontSize: 24 }}>{currentSecond}</Text>
			{/* <TouchableOpacity
				onPress={fetchData}
				style={{ width: 100, height: 100, backgroundColor: "red" }}
			/> */}
		</View>
	);
}
export default UseHooks;
